Get-ScheduledTask | Where-Object { $_.TaskPath -eq '\CAU\PostUpdateTasks\' } | ForEach-Object { $_ | Start-ScheduledTask; $State = $_ | Get-ScheduledTask | Select-Object -ExpandProperty 'State'; While ($State -ne 'Ready') { $State = $_ | Get-ScheduledTask | Select-Object -ExpandProperty 'State' } }
